		release.setAnnounceDate("2017 September");
		release.setPriceEur("480");
		
		Mobile mobile= new Mobile();
		mobile.setId(20504);
		mobile.setBrand("Apple");
		mobile.setPhone("Apple Watch Series 3 Aluminum");
		mobile.setPicture("https://cdn2.gsmarena.com/vv/bigpic/apple-watch-series3-sport-.jpg");
		mobile.setSim("eSIM");
		mobile.setResolution("390 x 312 pixels");		
		mobile.setRelease(release);
		mobile.setHardware(hardware);
		
		List<Mobile> mockMobileList = new ArrayList<Mobile>();
		mockMobileList.add(mobile);

		
		Mockito.when(mobileService.findMobiles(Mockito.anyMap(), Mockito.anyMap(), Mockito.anyMap())).thenReturn(mockMobileList);
		
		//Test many params
		String URI = "http://localhost:8090/mobile/find?id=20504&priceEur=480&announceDate=2017 September&gps=Yes with A-GPS";
		RequestBuilder requestBuilder = MockMvcRequestBuilders.get(
				URI).accept(
				MediaType.APPLICATION_JSON);
		
		MvcResult result = mockMvc.perform(requestBuilder).andReturn();
		String expectedJson = Utils.convertObjectToString(mockMobileList);
		String outputInJson = result.getResponse().getContentAsString();
		assertThat(outputInJson).isEqualTo(expectedJson);
		
		//Test Wrong Params
		URI = "http://localhost:8090/mobile/find?test=123";
		requestBuilder = MockMvcRequestBuilders.get(
				URI).accept(
				MediaType.APPLICATION_JSON);
		
		result = mockMvc.perform(requestBuilder).andReturn();
		expectedJson = "{\"_embedded\":{\"errors\":[{\"message\":\"Bad Request Parameters: [test]\",\"logref\":\"Bad Request Parameters: [test]\"}]}}";
		outputInJson = result.getResponse().getContentAsString();
		assertThat(outputInJson).isEqualTo(expectedJson);
		
		//Test one param
